import React, { useEffect, useState } from "react";
import { URL } from "../Service/Api";
import Header from "./Header";
import Card from "../components/Card";

const Home = () => {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    getAllData(searchQuery);
  }, [currentPage, searchQuery]);

  async function getAllData(query) {
    try {
      const searchParam = query ? `&query=${query}` : "";
      const apiData = await fetch(`${URL}&page=${currentPage}${searchParam}`);
      const res = await apiData.json();
      setData(res.results);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1); // Reset to the first page when searching
  };

  const goToNextPage = () => setCurrentPage((prev) => prev + 1);
  const goToPreviousPage = () => currentPage > 1 && setCurrentPage((prev) => prev - 1);

  return (
    <>
      <Header onSearch={handleSearch} />
      <div className="cards-container">
        {data.map((item) => (
          <Card {...item} key={item.id} />
        ))}
      </div>
      <div className="pagination">
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button onClick={goToNextPage}>Next</button>
      </div>
    </>
  );
};

export default Home;
