import React, { useEffect, useState } from "react";
import { URL } from "../Service/Api";
import Header from "./Header";
import Card from "../components/Card";

const Home = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    getAllData();
  }, [currentPage, searchQuery]); // Fetch data whenever page or searchQuery changes

  async function getAllData() {
    try {
      const query = searchQuery ? `&query=${encodeURIComponent(searchQuery)}` : ""; // Only add query if it exists
      const apiData = await fetch(`${URL}&page=${currentPage}${query}`);
      const res = await apiData.json();

      // Ensure results are available before updating state
      if (res.results) {
        setData(res.results);
        setFilteredData(res.results);
      } else {
        setFilteredData([]);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setFilteredData([]);
    }
  }

  const handleSearch = (query) => {
    setSearchQuery(query); // Update the search query
    setCurrentPage(1); // Reset to page 1 on search
  };

  const goToNextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Header onSearch={handleSearch} />
      <div className="cards-container">
        {filteredData?.map((item) => (
          <Card {...item} key={item.id} />
        ))}
      </div>
      <div className="pagination">
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button onClick={goToNextPage}>Next</button>
      </div>
    </>
  );
};

export default Home;
