async function getAllData(query) {
  try {
    const searchParam = query ? `&query=${query}` : "";
    const apiData = await fetch(`${TOP_RATED_URL}&page=${currentPage}${searchParam}`);
    const res = await apiData.json();
    setData(res.results);
  } catch (error) {
    console.error("Error fetching data:", error);
  }
}

const handleSearch = (query) => {
  setSearchQuery(query);
  setCurrentPage(1);
};
