import React, { useEffect, useState } from "react";
import { URL } from "../Service/Api"; // Make sure this URL is correct
import Header from "./Header";
import Card from "../components/Card";

const Home = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // Fetch data when page or searchQuery changes
  useEffect(() => {
    getAllData();
  }, [currentPage, searchQuery]);

  // Function to get data from the API
  const getAllData = async () => {
    let url = `${URL}&page=${currentPage}`; // Basic URL for fetching data
    if (searchQuery.trim()) {
      url += `&query=${searchQuery.trim()}`; // Append the search query if it's not empty
    }

    const apiData = await fetch(url);
    const res = await apiData.json();

    setData(res.results); // Set data from the response
    setFilteredData(res.results); // Set filtered data (initially all data)
  };

  // Handle the search input
  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1); // Reset to page 1 on new search
  };

  // Go to the next page
  const goToNextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  // Go to the previous page
  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Header onSearch={handleSearch} />
      <div className="cards-container">
        {filteredData.length === 0 ? (
          <div>No results found</div> // Show a message if no data is available
        ) : (
          filteredData.map((item) => (
            <Card {...item} key={item.id} />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={goToPreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button onClick={goToNextPage}>Next</button>
      </div>
    </>
  );
};

export default Home;
