import React, { useState } from "react";
import { Link } from "react-router-dom";

const Header = ({ onSearch }) => {
  const [search, setSearch] = useState("");

  const handleInputChange = (e) => {
    setSearch(e.target.value); // Update search state as user types
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent page reload
    onSearch(search); // Pass search query to the parent component
  };

  return (
    <>
      <div className="container">
        <div className="navbar">
          <h2 className="logo-name">MOVIE<span>DB</span></h2>
          <div className="nav-items">
            <ul className="nav-items">
              <li className="items">
                <Link to="/" className="items">
                  <h3>Popular</h3>
                </Link>
              </li>
              <li className="items">
                <Link to="/top-rated" className="items">
                  <h3>Top Rated</h3>
                </Link>
              </li>
              <li className="items">
                <Link to="/upcoming" className="items">
                  <h3>Upcoming</h3>
                </Link>
              </li>
            </ul>
            <form className="example" onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Search for Movies"
                name="search"
                className="input-field"
                value={search}
                onChange={handleInputChange} // Update the state on each change
              />
              <button type="submit" className="search-button">
                Search
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Header;
